mapper.select:
select id,uuid,phone 
from cms_minisite_user_action_phone_rela
where uuid = #{uuid}
and phone = #{phone}

SELECT count(distinct uuid)
FROM(
SELECT
*
FROM `cms_minisite_user_action_v3`
WHERE info is not null
AND
SUBSTRING_INDEX(info,"|",1) = #{postId}
AND
idtype = #{idtype}

AND
channel = #{channel}

) t

select
substring_index(substring_index(t.info, '|', 2), '|', - 1)
from (SELECT * FROM cms_minisite_user_action_v2 where idtype = #{idtype} and info is not null) t ORDER BY t.datetime;
